import styles from "./sidebar.module.scss";

import { TweenMax, TimelineLite, Power3 } from "gsap";
import { useRef, useEffect } from "react";

export default function Sidebar() {
  let image = useRef(null);
  let links = useRef(null);

  let tl = new TimelineLite();
  useEffect(() => {
    tl.from(image, 1.2, { x: -1280, ease: Power3.easeOut }, 1).from(
      links,
      1.2,
      { x: -1280, ease: Power3.easeOut }
    );
  });

  return (
    <div className={styles.sidebar_container}>
      <div ref={(el) => (image = el)} className={styles.sidebar_img}>
        <img src="/assets/craftbeer2.png" />
      </div>
      <div ref={(el) => (links = el)} className={styles.sidebar_links}>
        <ul>
          <li>Home</li>
          <li>About Us</li>
          <li>Our Beers</li>
          <li>History</li>
          <li>Contact</li>
        </ul>
      </div>
    </div>
  );
}
